package com.example.helperapp;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;import com.google.firebase.firestore.FirebaseFirestore;public class SignUpActivity extends AppCompatActivity {    private FirebaseAuth mAuth;    private FirebaseFirestore db;    @Override    protected void onStart() {        mAuth = FirebaseAuth.getInstance();        db = FirebaseFirestore.getInstance();        super.onStart();    }    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_sign_up);        EditText nameEt = findViewById(R.id.name_et);        EditText phoneNumberEt = findViewById(R.id.phone_number_et);        EditText emailEt = findViewById(R.id.email_et);        EditText passwordEt = findViewById(R.id.password_et);        EditText confirmPasswordEt = findViewById(R.id.confirm_password_et);        Button submitBtn = findViewById(R.id.submit_btn);        submitBtn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String name = nameEt.getText().toString();                String phoneNumber = phoneNumberEt.getText().toString();                String email = emailEt.getText().toString();                String password = passwordEt.getText().toString();                String confirmPassword = confirmPasswordEt.getText().toString();                if (name.isEmpty() || phoneNumber.isEmpty() || email.isEmpty() || password.isEmpty()) {                    Toast.makeText(SignUpActivity.this, "Invalid Values!", Toast.LENGTH_SHORT).show();                    return;                }            }        });    }}